const data = this.getValues();
const exchangeRate = data.exchange_rate;
const items = data.table_sqt;
let totalGross = 0;
let totalDiscount = 0;
let totalTax = 0;
let totalAmount = 0;

if (Array.isArray(items)) {
    items.forEach((item, index) => {
        // Ensure values are numeric
        const quantity = Number(item.quantity) || 0;
        const unitPrice = parseFloat(item.unit_price) || 0;

        const grossValue = quantity * unitPrice;
        totalGross += grossValue;

        // Set gross value
        this.setData({
            [`table_sqt.${index}.sqt_gross`]: grossValue,
            [`table_sqt.${index}.total_price`]: grossValue,
            'sqt_sub_total': totalGross
        });

        // Get discount, discountUOM, and tax info for this row
        let discount = parseFloat(item.sqt_discount) || 0;
        const discountUOM = item.sqt_discount_uom_id;
        const taxRate = Number(item.sqt_tax_rate_percent) || 0;
        const taxInclusive = item.sqt_tax_inclusive;
        let discountAmount = 0.00;

        if(discountUOM){
            // Calculate discount amount using UOM from database
            if (discount !== 0) {
                if (discountUOM === 'Amount') {
                    discountAmount = discount;
                } else if (discountUOM === '%') {
                    discountAmount = (grossValue * discount) / 100;
                }

                if (discountAmount > grossValue) {

                    discount = 0;
                    discountAmount = 0;

                    this.setData({
                        [`table_sqt.${index}.sqt_discount`]: 0,
                        [`table_sqt.${index}.sqt_discount_amount`]: 0
                    });
                } else {
                    this.setData({
                        [`table_sqt.${index}.sqt_discount_amount`]: parseFloat(discountAmount.toFixed(2))
                    });
                }
            }
        }

        else {
            this.setData({
                [`table_sqt.${index}.sqt_discount_amount`]: 0
            });
        }
        // Calculate amount after discount
        const amountAfterDiscount = grossValue - discountAmount;

        // Calculate tax amount based on taxInclusive flag
        let taxAmount = 0;
        let finalAmount = amountAfterDiscount;

        if (taxRate) {
            const taxRateDecimal = taxRate / 100;

            if (taxInclusive === 1) {
                // Tax inclusive calculation
                taxAmount = amountAfterDiscount - (amountAfterDiscount / (1 + taxRateDecimal));
                finalAmount = amountAfterDiscount;
            } else {
                // Tax exclusive calculation
                taxAmount = amountAfterDiscount * taxRateDecimal;
                finalAmount = amountAfterDiscount + taxAmount;
            }

            // Set tax amount
            this.setData({
                [`table_sqt.${index}.sqt_taxes_fee_amount`]: parseFloat(taxAmount.toFixed(2))
            });
        } else {
            this.setData({
                [`table_sqt.${index}.sqt_taxes_fee_amount`]: 0
            });
        }

        // Set final amount
        this.setData({
            [`table_sqt.${index}.total_price`]: parseFloat(finalAmount.toFixed(2))
        });

        // Subtract previous values before adding new ones
        totalDiscount += discountAmount;
        totalTax += taxAmount;
        totalAmount += finalAmount;

        if(totalTax > 0) {
            this.display(['sqt_total_tax','total_tax_currency']);
        }

        // Set the total fields
        this.setData({
            'sqt_total_discount': parseFloat(totalDiscount.toFixed(2)),
            'sqt_total_tax': parseFloat(totalTax.toFixed(2)),
            'sqt_totalsum': parseFloat(totalAmount.toFixed(2))
        });

        if(!exchangeRate){
            return
        } else {
            this.setData({'myr_total_amount': exchangeRate * parseFloat(totalAmount.toFixed(2))})
        }
    }); // This closing bracket was missing for the forEach callback


} else {
    console.log("Not an array:", items);
}

